class Emitter {
  constuctor() {
    this.queue = [];
  }

  on(obj) {
    this.queue.push(obj);
  }

  notify() {
    this.queue.forEach(obj => obj.fn());
  }

  off(name) {
    const index = this.queue.indexOf(obj => obj.name === name);
    this.queue.splice(index, 1);
  }
}

const emitter = new Emitter();
emitter.on({
  name: "test1",
  fn: () => {}
});

emitter.on({
  name: "test2",
